import { Card, Input, TextField } from '@material-ui/core'
import React, { useState } from 'react'
import Button from 'reactstrap/lib/Button';

export default function Feedbackform(props) {

    const [formDetails, setFormDetails] = useState();
    const data = JSON.parse("../resources/input.json");

    console.log(data);

    const handleTextFieldChange = (e) => {
        setFormDetails({ ...formDetails, companyName: e.target.value });
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        const route = `${window.location.origin}/api`;
        const requestOptions = {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify(formDetails)
        };
        const response = await fetch(route + '/quotations', requestOptions);
        const data = await response.json();
    }

    return (
        <div style={{ width: '100%', display: 'flex', flexDirection: 'column', gap: '20px', justifyContent: 'center', alignContent: 'center', margin: '20px' }}>
            <h2>Form Details</h2>
            <form onSubmit={handleSubmit}>
                <div className="col">
                    <TextField
                        label="Company Name"
                        value={formDetails && formDetails.companyName ? formDetails.companyName : ""}
                        onChange={handleTextFieldChange}
                    />
                </div>
                <div className="col" style={{ marginTop: '16px', marginLeft: 'auto' }}>
                    <Button variant="contained" color="primary" type="submit">Submit</Button>
                </div>
            </form>
        </div>

    )
}
