import { Card, FormControlLabel, Input, Radio, RadioGroup, TextField } from '@material-ui/core'
import React, { useState } from 'react'
import Button from 'reactstrap/lib/Button';
import patientData from "../resources/input.json";


export default function Feedbackform(props) {

    const [formDetails, setFormDetails] = useState({ name: patientData.entry[0].resource.name[0].text, recommendation: 1, managediagonsis: "No", feedback: "" });

    console.log(patientData.id);

    const handleRecommendationChange = (e) => {
        setFormDetails({ ...formDetails, recommendation: e.target.value });
    }

    const handleChange = (e) => {
        setFormDetails({ ...formDetails, managediagonsis: e.target.value });
    }

    const handleFeedBackFieldChange = (e) => {
        setFormDetails({ ...formDetails, feedback: e.target.value });
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        const route = `http://localhost:8080/api/v1/patientfeedback/add`;
        const requestOptions = {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Access-Control-Allow-Origin': '*'
            },
            body: JSON.stringify(formDetails),
        };
        const response = await fetch(route, requestOptions);
        const data = await response.json();
    }

    return (
        <div style={{ width: '100%', display: 'flex', flexDirection: 'column', gap: '20px', justifyContent: 'center', alignContent: 'center', margin: '20px' }}>
            <h2>Form Details</h2>
            <form onSubmit={handleSubmit}>
                <div className="col">
                    <label>
                        Hi {patientData.entry[0].resource.name[0].text}, on a scale of 1-10, would you recommend Dr [Doctor Last Name] to a friend or family member? 1 = Would not recommend, 10 = Would strongly recommend
                    </label>
                    <div>
                        <TextField
                            type="number"
                            InputProps={{ inputProps: { min: 0, max: 10 } }}
                            value={formDetails.recommendation}
                            onChange={handleRecommendationChange}
                        />
                    </div>
                </div>
                {formDetails.recommendation &&
                    <div className="col">
                        <label>
                            Thank you. You were diagnosed with [Diagnosis]. Did Dr [Doctor Last Name] explain how to manage this diagnosis in a way you could understand?
                        </label>
                        <div>
                            <RadioGroup
                                aria-label="gender"
                                name="controlled-radio-buttons-group"
                                value={formDetails.managediagonsis}
                                onChange={handleChange}
                            >
                                <FormControlLabel value="Yes" control={<Radio />} label="Yes" />
                                <FormControlLabel value="No" control={<Radio />} label="No" />
                            </RadioGroup>
                        </div>
                    </div>

                }

                <div className="col">
                    <label>
                        We appreciate the feedback, one last question: how do you feel about being diagnosed with [Diagnosis]?
                    </label>
                    <div>
                        <TextField
                            value={formDetails.feedback}
                            onChange={handleFeedBackFieldChange}
                        />
                    </div>
                </div>
                <div className="col">
                    <label>
                        Thanks again! Here’s what we heard:
                    </label>
                    <div>
                    </div>
                </div>



                <div className="col" style={{ marginTop: '16px', marginLeft: 'auto' }}>
                    <Button variant="contained" color="primary" type="submit">Submit</Button>
                </div>
            </form>
        </div>

    )
}
